function loadPageMeta(t){document.title=t.title,document.querySelector('meta[name="description"]').setAttribute("content",t.description)}function loadHeader(t){document.querySelector("[data-logo]").src=t.logo,document.querySelector("[data-main-title]").textContent=t.mainTitle,document.querySelector("[data-subtitle]").textContent=t.subtitle}function loadCheckEmail(t){document.querySelector("[data-email-title]").textContent=t.title,document.querySelector("[data-email-text]").textContent=t.text}function loadChecklistPreview(t){document.querySelector("[data-checklist-title]").textContent=t.title;const e=document.querySelector("[data-checklist-items]");e.innerHTML="",t.items.forEach(t=>{e.innerHTML+=`\n            <div class="checklist-item">\n                <div class="check-icon"></div>\n                <div class="checklist-item-content">\n                    <h3>${t.title}</h3>\n                    <p>${t.description}</p>\n                </div>\n            </div>\n        `})}function loadNextSteps(t){document.querySelector("[data-steps-title]").textContent=t.title;const e=document.querySelector("[data-next-steps]");e.innerHTML="",t.steps.forEach(t=>{e.innerHTML+=`\n            <div class="step-card">\n                <div class="step-number">${t.number}</div>\n                <h3>${t.title}</h3>\n                <p>${t.description}</p>\n            </div>\n        `})}function loadCTA(t){document.querySelector("[data-cta-title]").textContent=t.title,document.querySelector("[data-cta-subtitle]").textContent=t.subtitle,document.querySelector("[data-cta-button]").textContent=t.buttonText,document.querySelector("[data-cta-button]").href=t.buttonUrl;const e=`\n        ${t.testimonial.quote}\n        <div class="testimonial-author">â€” ${t.testimonial.author}</div>\n    `;document.querySelector("[data-testimonial]").innerHTML=e}function loadFooter(t){document.querySelector("[data-footer-text]").textContent=t.text}document.addEventListener("DOMContentLoaded",async function(){try{const t=await fetch("data/thank-you-page.json"),e=await t.json();loadPageMeta(e.page),loadHeader(e.header),loadCheckEmail(e.checkEmail),loadChecklistPreview(e.checklistPreview),loadNextSteps(e.nextSteps),loadCTA(e.cta),loadFooter(e.footer)}catch(t){console.error("Error loading page content:",t)}});